<?php/** * admin各会社ウェブサイト一覧を取得 *  * @access public * @author 川端洋平 * @version 0.0.1 * @since 2012/07/25 *///// 設定ファイル読み込み ////require_once '../../lib-machine.php';try {    //// 認証 ////    // Auth::isAuth('machine');        $action = Req::query('action');    $target = Req::query('target');    $data   = Req::query('data');        $res = '';        if ($action == "getMachineList") {        $mModel = new Machine();        $res    = $mModel->search(array('company_id' => $data['company_id']));    } else if ($action == "getMachine") {        $mModel = new Machine();        $res    = $mModel->get($data['id'], $data['company_id']);    }        echo json_encode($res);} catch (Exception $e) {    echo $e->getMessage();}