<?php/** * admin用一覧を取得 *  * @access public * @author 川端洋平 * @version 0.0.1 * @since 2012/07/25 *///// 設定ファイル読み込み ////require_once '../../lib-machine.php';try {    //// 認証 ////    Auth::isAuth('machine');        $action = Req::post('action');    $target = Req::post('target');    $data   = Req::post('data');        // $eModel = new Eips();    if ($action == "getList") {        $gModel = new Catalog();        $result = $gModel->getList(array('model'=>$data['mo']));    }        echo json_encode($result);} catch (Exception $e) {    echo $e->getMessage();}